import { FC, useState, useEffect } from "react";
import React from "react";
import { ApiServiceImpl } from "../../api/api-service";
import { isPartnerValid, Partner, partnerValidity } from "../../api/domain/partner";
import { PartnerScreen } from "../partner-screen";

export const PartnerScreenInfo: FC<{ id: string }> = ({ id }) => {

  const [dataPartner, setDataPartner] = useState<Partner>();

  useEffect(() => {
    ApiServiceImpl.instance.getPartner(id).then((res) => {
      setDataPartner(res);
    });
  }, [id]);


    const update = (partner: Partner) => {
    if (isPartnerValid(partnerValidity(partner))) {
    ApiServiceImpl.instance.updatePartner(partner).then((res)=>{
      console.log(res)
      window.location.href="/";
    })
  }
  }


  return (
    <>
      {dataPartner && (
        <PartnerScreen partner={dataPartner} partnerUpdate={update}></PartnerScreen>
      )}
    </>
  );
};
