import React from 'react';
import { FC } from "react";
import { AddressInfoForm } from "./partner-screen-components/address-info-form"
import { CompanyInfoForm } from './partner-screen-components/company-info-form';
import { ContactInfoForm } from './partner-screen-components/contact-info-form';
import { PersonalInfoForm } from './partner-screen-components/personal-info-form';
import { PartnerTypeForm } from './partner-screen-components/partner-type-form';
import { Partner } from '../api/domain/partner';
import Button from 'react-bootstrap/Button'
import { Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export interface PartnerProps {
    partner: Partner;
    partnerUpdate(partner: Partner): void;

  }


export const PartnerScreen : FC<PartnerProps> = ({partner, partnerUpdate}) => {




return(
    <>
    <Container className="form__container">
    <PartnerTypeForm
    partnerType={partner.partnerType}
    />
   {partner.companyInfo && (<CompanyInfoForm
    companyInfo={partner.companyInfo}
    />)}
    {partner.personalInfo && (<PersonalInfoForm
    personalInfo={partner.personalInfo}
    />)}
    <ContactInfoForm
    contactInfo={partner.contactInfo}
    />
    <AddressInfoForm
    addressInfo={partner.addressInfo}
    />
    <div className="pull-right">
<Link to={'/'}> <Button variant="secondary" className="cancel__button">Отмена</Button></Link>
<Button variant="primary" className="update__button">Сохранить</Button>
</div>
</Container>
    </>
)    
}
