import React from 'react';
import Pagination from 'react-bootstrap/Pagination'
import { FC } from "react";

export interface TablePaginationProps {
    currentPage: number;
    pageCount: number;
    changePage(page: number): void;
}

export const TablePagination : FC<TablePaginationProps> = ({currentPage, pageCount, changePage}) => {
let active = 1;
let items = []
for (let i = 1; i <= pageCount; i++) {
   items.push(i);
 }



    return (
        <div>        
        <Pagination>
            {items.map(item => {


                return (
                <Pagination.Item 
                    onClick={changePage()}
                    active={currentPage === active}
                    key={item}>{item +1}</Pagination.Item>
                );
            })} 
        </Pagination>
      </div>
    ) }
