import React, { ChangeEvent } from "react";
import { FC } from "react";
import Form from 'react-bootstrap/Form'
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { AddressInfo } from '../../api/domain/address-info';
import { useState } from 'react';



export interface AddressInfoFormProps {
  addressInfo: AddressInfo;
  onAddressInfoChange(value:AddressInfo): void;
}



export const AddressInfoForm: FC<AddressInfoFormProps> = ({addressInfo, onAddressInfoChange}) => {

const [city, setCity] = useState(addressInfo.city);
const [street, setStreet] = useState(addressInfo.street);
const [houseNumber, setHouseNumber] = useState(addressInfo.houseNumber);
const [idx, setIdx] = useState(addressInfo.idx);


const getCurrentAddressInfo = (): AddressInfo => ({
city, street, houseNumber, idx
})


const onCityChange = (city:string) => {
setCity(city);
const ai = {...getCurrentAddressInfo(), ...{city}};
onAddressInfoChange(ai);
}
const onStreetChange = (street: string) => {
  setStreet(street);
  const ai = {...getCurrentAddressInfo(), ...{street}};
  onAddressInfoChange(ai);
}
const onHouseNumberChange = (event: ChangeEvent<HTMLInputElement>) => {

  const houseNumber = event.target.value;
  console.log(houseNumber)
setHouseNumber(houseNumber);
const ai = {...getCurrentAddressInfo(), ...{houseNumber}};
onAddressInfoChange(ai);
}
const onIdxChange = (idx: string) => {
  setIdx(idx);
  const ai = {...getCurrentAddressInfo(), ...{idx}};
  onAddressInfoChange(ai);
}


const test = (evt:any) => console.log(evt);

    return(
        <>
            <h2>Адрес</h2>
    <Form>
  <Form.Group as={Row} controlId="formPlaintextCity" >
    <Form.Label column sm="2">
      Город
    </Form.Label>
    <Col sm="10">
    <Form.Control  
    type="text" 
    value={city} 
    placeholder='Город'
    required
    />
    </Col>
  </Form.Group>
  <Form.Group as={Row} controlId="formPlaintextStreet"  onChange={onStreetChange}>
    <Form.Label column sm="2">
      Улица
    </Form.Label>
    <Col sm="10">
      <Form.Control   
      value={street}
      type="text"
      required
      placeholder='Улица'
      />
    </Col>
  </Form.Group>
  <Form.Group as={Row} controlId="formPlaintextHouseNumber" >
    <Form.Label column sm="2">
      Номер дома
    </Form.Label>
    <Col sm="10">
      <Form.Control   
      onChange={onHouseNumberChange}
      value={houseNumber}
      type='string'
      placeholder="Номер дома"
      required
      />
    </Col>
  </Form.Group>
  <Form.Group as={Row} controlId="formPlaintextIdx" onChange={onIdxChange}>
    <Form.Label column sm="2">
      Индекс
    </Form.Label>
    <Col sm="10">
      <Form.Control   
      type="string"
      placeholder="Индекс"
      required
      value={idx} />
    </Col>
  </Form.Group>
</Form>
</>
    )

    }
