import { FC, useState, useEffect } from "react";
import React from 'react';
import { PartnersPage } from "../api/domain/partners-page";
import { ApiServiceImpl } from "../api/api-service";
import { Table } from 'react-bootstrap';
import { PartnerType } from "../api/domain";
import Container from 'react-bootstrap/Container'
import { TablePagination } from './pagination';
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import { ModalWindow } from "./modal-window";

var FA = require('react-fontawesome')

const deleteQuestion = "Вы точно хотите удалить данные о партнере?"

export const PartnersTable : FC = () => {
  const [dataPage, setDataPage] = useState<PartnersPage>();
  const [currentPage, setCurrentPage] = useState(0);
  const [isOpened, setShowModal] = useState(false);

  const handleOnOpen = () => {
    setShowModal(true);
  }

  const handleOnClose = (answer: boolean) => {
setShowModal(false);
if (!answer){
  return false
}
  }

    useEffect(() => {
        ApiServiceImpl.instance.getPartners(currentPage, 10)
        .then(res => setDataPage(res))
    }, [currentPage]);
    const handlePageChange = (page: number) => {
      setCurrentPage(page)
    }
        return (
        <Container fluid className="mt-3">
        <Table striped bordered hover>
        <thead>
          <tr>
            <th>Имя партнера</th>
            <th>Тип партнера</th>
            <th>Город</th>
            <th>Адрес</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {dataPage?.items.map(item => {
            return (<tr key={item.id}>
              <td>{item.displayName}</td>
              <td>{item.partnerType === PartnerType.legalEntity ? "Юридическое лицо" : "Физическое лицо"}</td>
              <td>{item.city}</td>
              <td>{item.address}</td>
              <td><button className="folder__button"><FA name=" fa-folder-open-o" className="folder__icon" /></button><button className="trash__button" onClick={handleOnOpen}><FA name=" fa fa-trash-o" className="trash__icon" /></button></td>
            </tr>);
          })}
        </tbody>
      </Table>
      <Row>
      <Col md={{ span: 11, offset: 11 }}>
      <TablePagination
      currentPage={currentPage}
      pageCount={dataPage?.pagesCount || 0}
      changePage={handlePageChange}> 
      </TablePagination>
      </Col>
      </Row>
      {
        isOpened && (
          <ModalWindow
          text={deleteQuestion}
          onClose={handleOnClose}
          ></ModalWindow>
        )
      }
      </Container>
    )

}
