import React, { useState, ChangeEvent } from "react";
import { FC } from "react";
import Form from 'react-bootstrap/Form'
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { ContactInfo } from '../../api/domain/contact-info'

export interface ContactInfoProps { 
contactInfo: ContactInfo;
onContactInfoChange(value: ContactInfo): void;
}



export const ContactInfoForm: FC<ContactInfoProps> = ({contactInfo, onContactInfoChange}) => {
const [email, setEmail] = useState(contactInfo.email);
const [phone, setPhone] = useState(contactInfo.phone);
const [validated, setValidated] = useState(false);

const handleSubmit = (event:any) => {
  const form = event.currentTarget;
  if (form.checkValidity() === false) {
    event.preventDefault();
    event.stopPropagation();
  }

  setValidated(true);
};


const getCurrentContactInfo = (): ContactInfo => ({
  email, phone
})


const onEmailChange = (event: ChangeEvent<HTMLInputElement>) => {
  const email = event.target.value
  setEmail(email);
  const ci = {...getCurrentContactInfo(), ...{email}};
  onContactInfoChange(ci);
}
const onPhoneChange = (event: ChangeEvent<HTMLInputElement>) => {
  const phone = event.target.value
  setPhone(phone);
  const ci = {...getCurrentContactInfo(), ...{phone}};
  onContactInfoChange(ci);
}


    return(
        <>
            <h2>Контакты</h2>
        <Form noValidate validated={validated} onSubmit={handleSubmit}>
        <Form.Group as={Row} controlId="formHorizontalEmail">
          <Form.Label column sm={2}>
            Электронная почта
          </Form.Label>
          <Col sm={10}>
            <Form.Control 
            type="email" 
            placeholder="Электронная почта"   
            value={email}
            onChange={onEmailChange}
            required/>
          </Col>
          <Form.Control.Feedback>данные введены верно</Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Row} controlId="formHorizontalPhone">
          <Form.Label column sm={2}>
            Телефон
          </Form.Label>
          <Col sm={10}>
            <Form.Control 
            type="phone" 
            placeholder="Телефон"   
            value={phone}
            onChange={onPhoneChange}
            required
            />
          </Col>
        </Form.Group>
        </Form>
        </>
    )

    }
