import { PartnersPage } from './domain/partners-page';
import { Partner } from "./domain";


export interface ApiService {
    getPartner(id: string): Promise<Partner>;
    deletePartner(id: string): Promise<boolean>; 
    updatePartner(partner: Partner): Promise<{id: string}>;
    createPartner(partner: Partner): Promise<{id: string}>;
    getPartners(page: number, pageSize: number): Promise<PartnersPage>;
}

export class ApiServiceImpl implements ApiService {

    private constructor(){
    }

    static readonly instance = new ApiServiceImpl();

    getPartner(id: string): Promise<Partner>{
     return  fetch(`http://localhost:3000/partner/${id}`, {
        method: 'GET'
    })
    .then(res => res.json())
    .then(res => { 
        console.log(res);
        return res as Partner;
    })
    }
    deletePartner(id: string): Promise<boolean> {
    return fetch(`http://localhost:3000/partner/${id}`, {
        method: 'DELETE'
    })
    .then(res => res.json())
    .then(res => {
        console.log(res);
        return res as boolean; 
    })
    }
    updatePartner(partner: Partner): Promise<{id: string}>{
        const method = 'POST';
        const headers = new Headers();
        headers.append('Content-Type', 'application/json');
        const body = JSON.stringify(partner);
        return fetch(`http://localhost:3000/partner/${partner.id}`, {method, headers, body})
        .then(res => res.json())
        .then(res => {
            console.log(res);
            return res; 
        })
    }
    createPartner(partner: Partner): Promise<{id: string}>{
        const method = 'PUT';
        const headers = new Headers();
        headers.append('Content-Type', 'application/json');
        const body = JSON.stringify(partner);
        return fetch(`http://localhost:3000/partner`, {method, headers, body})
        .then(res => res.json())
        .then(res => {
            console.log(res);
            return res; 
        })
    }
    getPartners(page: number, pageSize: number): Promise<PartnersPage>{
return fetch(`http://localhost:3000/partners?page=${page}&pageSize=${pageSize}`, {
    method: 'GET'
})
.then(res => res.json())
.then(res => {
    console.log(res);
    return res as PartnersPage;
})
    }
}
