import { FC, useState, useEffect } from "react";
import React from "react";
import { PartnersPage } from "../../api/domain/partners-page";
import { ApiServiceImpl } from "../../api/api-service";
import { Table } from "react-bootstrap";
import { PartnerShortInfo, PartnerType } from "../../api/domain";
import Container from "react-bootstrap/Container";
import { TablePagination } from "../pagination/pagination";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { ModalWindow } from "../modals/modal-window";
import { Link } from "react-router-dom";
import Button from "react-bootstrap/Button";
var FA = require("react-fontawesome");

export const PartnersTable: FC = () => {
  const [dataPage, setDataPage] = useState<PartnersPage>();
  const [currentPage, setCurrentPage] = useState(0);
  const [
    partnerToRemove,
    setPartnerToRemove,
  ] = useState<PartnerShortInfo | null>(null);
  const isOpened = !!partnerToRemove;
  const deleteQuestion = `Вы точно хотите удалить данные о ${partnerToRemove?.displayName}?`;

  const handleOnOpen = (partner: PartnerShortInfo) => {
    setPartnerToRemove(partner);
  };

  const handleOnClose = (answer: boolean) => {
    if (!answer) {
      setPartnerToRemove(null);
      return;
    } else {
      ApiServiceImpl.instance.deletePartner(partnerToRemove!.id).then((res) => {
        setPartnerToRemove(null);
        if (res) {
          //@ts-ignore
          setCurrentPage(new Number(currentPage));
        }
      });
    }
  };

  useEffect(() => {
    ApiServiceImpl.instance.getPartners(currentPage, 3).then((res) => {
      setDataPage(res);
      if (currentPage >= res.pagesCount) {
        setCurrentPage(currentPage - 1);
      }
    });
  }, [currentPage]);

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  return (
    <>
      <Container fluid className="mt-3">
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Имя партнера</th>
              <th>Тип партнера</th>
              <th>Город</th>
              <th>Адрес</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {dataPage?.items.map((item) => {
              return (
                <tr key={item.id}>
                  <td>{item.displayName}</td>
                  <td>
                    {item.partnerType === PartnerType.legalEntity
                      ? "Юридическое лицо"
                      : "Физическое лицо"}
                  </td>
                  <td>{item.city}</td>
                  <td>{item.address}</td>
                  <td>
                    <Link to={`/partner/${item.id}`}>
                      <button className="folder__button">
                        <FA name=" fa-folder-open-o" className="folder__icon" />
                      </button>
                    </Link>
                    <button
                      className="trash__button"
                      onClick={() => handleOnOpen(item)}
                    >
                      <FA name="fa fa-trash-o" className="trash__icon" />
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>

        <Row>
          <Col>
          <Link to='/partner-create/'>
            <Button variant="primary">Добавить партнера</Button>
            </Link>
            <div className="pull-right">
              <TablePagination
                currentPage={currentPage}
                pageCount={dataPage?.pagesCount || 0}
                changePage={handlePageChange}
              ></TablePagination>
            </div>
          </Col>
        </Row>
      </Container>
      {isOpened && (
        <ModalWindow
          text={deleteQuestion}
          onClose={handleOnClose}
        ></ModalWindow>
      )}
    </>
  );
};
