import { FC, useState, useEffect } from "react";
import React from 'react';
import { PartnersPage } from "../api/domain/partners-page";
import { ApiServiceImpl } from "../api/api-service";
import { Table } from 'react-bootstrap';
import { PartnerType } from "../api/domain";
import Container from 'react-bootstrap/Container'
import { TablePagination } from './pagination';
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import FontAwesome from 'react-fontawesome'


export const PartnersTable : FC = () => {
  const FA = require('react-fontawesome')
  const [dataPage, setDataPage] = useState<PartnersPage>();
  const [currentPage, setCurrentPage] = useState(0);
    useEffect(() => {
        ApiServiceImpl.instance.getPartners(currentPage, 10)
        .then(res => setDataPage(res))
    }, [currentPage]);
    const hendlePageChange = (page: number) => {
      setCurrentPage(page)
    }
        return (
        <Container fluid className="mt-3">
        <Table striped bordered hover>
        <thead>
          <tr>
            <th>Имя партнера</th>
            <th>Тип партнера</th>
            <th>Город</th>
            <th>Адрес</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {dataPage?.items.map(item => {
            return (<tr key={item.id}>
              <td>{item.displayName}</td>
              <td>{item.partnerType === PartnerType.legalEntity ? "Юридическое лицо" : "Физическое лицо"}</td>
              <td>{item.city}</td>
              <td>{item.address}</td>
              <td><button><i className="fa fa-folder-open-o" aria-hidden="true"></i></button><button></button></td>
            </tr>);
          })}
        </tbody>
      </Table>
      <Row>
      <Col md={{ span: 11, offset: 11 }}>
      <TablePagination
      currentPage={currentPage}
      pageCount={dataPage?.pagesCount || 0}
      changePage={hendlePageChange}> 
      </TablePagination>
      </Col>
      </Row>
      </Container>
    )

}
