import React, { useState } from 'react';
import { FC } from "react";
import { AddressInfoForm } from "./partner-screen-components/address-info-form"
import { CompanyInfoForm } from './partner-screen-components/company-info-form';
import { ContactInfoForm } from './partner-screen-components/contact-info-form';
import { PersonalInfoForm } from './partner-screen-components/personal-info-form';
import { PartnerTypeForm } from './partner-screen-components/partner-type-form';
import { Partner } from '../api/domain/partner';
import Button from 'react-bootstrap/Button'
import { Container } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { CompanyInfo, PersonalInfo, ContactInfo, AddressInfo } from '../api/domain';
import { personalInfoValidation } from '../api/domain/personal-info';
import { companyInfoValidation } from '../api/domain/company-info';
import { contactInfoValidation } from '../api/domain/contact-info';
import { validateAddressInfo } from '../api/domain/address-info';


export interface PartnerProps {
    partner: Partner;
    partnerUpdate(partner: Partner): void;

  }


export const PartnerScreen : FC<PartnerProps> = ({partner, partnerUpdate}) => {

const [changedPartner, setChangedPartner] = useState(partner);



const handleUpdate = () => {
    partnerUpdate(changedPartner);
}



const handleCompanyInfoChange = (companyInfo: CompanyInfo) => {
    setChangedPartner({...changedPartner, ...{companyInfo}});
}

const handlePersonalInfoChange = (personalInfo: PersonalInfo) => {
    setChangedPartner({...changedPartner, ...{personalInfo}});
}

const handleContactInfoChange = (contactInfo: ContactInfo) => {
    setChangedPartner({...changedPartner, ...{contactInfo}});
}

const handleAddressInfoChange = (addressInfo: AddressInfo) => {
    setChangedPartner({...changedPartner, ...{addressInfo}});
}

const hendlePersonalInfoValidation = () => {
    personalInfoValidation(changedPartner.personalInfo!)
}
const hendleCompanyInfoValidation = () => {
    companyInfoValidation(changedPartner.companyInfo!)
}
const hendleContactInfoValidation = () => {
    contactInfoValidation(changedPartner.contactInfo!)
}
const hendleAddressInfoValidation = () => {
    validateAddressInfo(changedPartner.addressInfo!)
}

return(
    <>
    <Container className="form__container">
    <PartnerTypeForm
    partnerType={partner.partnerType}
    />
   {partner.companyInfo && (<CompanyInfoForm
    companyInfo={partner.companyInfo}
    isValid={hendleCompanyInfoValidation}
    onCompanyInfoChange={handleCompanyInfoChange}
    />)}
    {partner.personalInfo && (<PersonalInfoForm
    isValid={hendlePersonalInfoValidation}
    personalInfo={partner.personalInfo}
    onPersonalInfoChange={handlePersonalInfoChange}
    />)}
    <ContactInfoForm
    isValid={hendleContactInfoValidation}
    contactInfo={partner.contactInfo}
    onContactInfoChange={handleContactInfoChange}
    />
    <AddressInfoForm
    isValid={hendleAddressInfoValidation}
    addressInfo={partner.addressInfo}
    onAddressInfoChange={handleAddressInfoChange}
    />
    <div className="pull-right">
<Link to={'/'}> <Button variant="secondary" className="cancel__button">Отмена</Button></Link>
<Button 
variant="primary" 
className="update__button"
onClick={handleUpdate}
type="submit"
>Сохранить</Button>

</div>
</Container>
    </>
)    
}
