import React, { useState } from "react";
import { FC } from "react";
import Form from 'react-bootstrap/Form'
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { PersonalInfo } from '../../api/domain/personal-info';
import { Gender } from "../../api/domain";


export interface PersonalInfoFormProps {
personalInfo: PersonalInfo;
onPersonalInfoChange(value: PersonalInfo): void;
}


  export const PersonalInfoForm: FC<PersonalInfoFormProps> = ({personalInfo, onPersonalInfoChange}) => {
const [lastName, setLastName] = useState(personalInfo.lastName);
const [firstName, setFirstName] = useState(personalInfo.firstName);
const [middleName, setMiddleName] = useState(personalInfo.middleName);
const [birthDate, setBirthDate] = useState(personalInfo.birthDate);
const [gender, setGender] = useState(personalInfo.gender);

const getCurrentPersonalInfo = (): PersonalInfo => ({
  lastName, firstName, middleName, birthDate, gender
})

const onLastNameChange = (lastName: string) => {
  setLastName(lastName);
  const pi = {...getCurrentPersonalInfo(), ...{lastName}};
  onPersonalInfoChange(pi);
} 

const onFirstNameChange = (firstName: string) => {
  setFirstName(firstName);
  const pi = {...getCurrentPersonalInfo(), ...{firstName}};
  onPersonalInfoChange(pi); 
}
const onMiddleNameChange = (middleName: string) => {
  setMiddleName(middleName);
  const pi = {...getCurrentPersonalInfo(), ...{middleName}};
  onPersonalInfoChange(pi);
}
const onBirthNameChange = (birthDate: string) => {
  setBirthDate(birthDate);
  const pi = {...getCurrentPersonalInfo(), ...{birthDate}};
  onPersonalInfoChange(pi);
}
const onGenderChange = (gender: Gender) => {
  setGender(gender);
  const pi = {...getCurrentPersonalInfo(), ...{gender}};
  onPersonalInfoChange(pi);
}


return(
    <>
        <h2>Персональная информация</h2>
    <Form>
  <Form.Group as={Row} controlId="formPlaintextLastName">
    <Form.Label column sm="2">
      Фамилия
    </Form.Label>
    <Col sm="10">
      <Form.Control   defaultValue={personalInfo.lastName} />
    </Col>
  </Form.Group>
  <Form.Group as={Row} controlId="formPlaintextFirstName">
    <Form.Label column sm="2">
      Имя
    </Form.Label>
    <Col sm="10">
      <Form.Control   defaultValue={personalInfo.firstName}/>
    </Col>
  </Form.Group>
  <Form.Group as={Row} controlId="formPlaintextMiddleName">
    <Form.Label column sm="2">
      Отчество
    </Form.Label>
    <Col sm="10">
      <Form.Control   defaultValue={personalInfo.middleName} />
    </Col>
  </Form.Group>
  <Form.Group as={Row} controlId="formPlaintextBirthDate">
    <Form.Label column sm="2">
      Дата рождения
    </Form.Label>
    <Col sm="10">
      <Form.Control   defaultValue={personalInfo.birthDate} />
    </Col>
  </Form.Group>
  <Form.Group controlId="formPlaintextGender" as={Row}>
  <Form.Label column sm="2">
      Пол
    </Form.Label>
    <Col sm="10">
    <Form.Control  as="select" readOnly >
    <option>{personalInfo.gender === Gender.male ? 'мужчина' : 'женщина'}</option>
    <option>{personalInfo.gender === Gender.female ? 'мужчина' : 'женщина'}</option>
    </Form.Control>
    </Col>
  </Form.Group>
</Form>
</>
)

}
