import { FC, useState, useEffect } from "react";
import React from "react";
import { ApiServiceImpl } from "../../api/api-service";
import { isPartnerValid, Partner, partnerValidity, PartnerValidity } from "../../api/domain/partner";
import { PartnerScreen } from "../partner-screen";

export const PartnerScreenInfo: FC<{ id: string }> = ({ id }) => {

  const [dataPartner, setDataPartner] = useState<Partner>();
  const [validPartner, setValidPartner] = useState<PartnerValidity>();
console.log(validPartner)


  useEffect(() => {
    ApiServiceImpl.instance.getPartner(id).then((res) => {
      setDataPartner(res);
    });
  }, [id]);



  const update = (partner: Partner) => {
    setValidPartner(partnerValidity(partner)) 

    if (isPartnerValid(validPartner!)) {
    ApiServiceImpl.instance.updatePartner(partner).then((res)=>{
      console.log(res)
      window.location.href="/";
    })
  } 
  }

  return (
    <>
      {dataPartner && (
        <PartnerScreen 
        partnerValidation={validPartner!}
        partner={dataPartner} 
        partnerUpdate={update}>
        </PartnerScreen>
      )}
    </>
  );
};
